mapper.insert:
INSERT INTO studydb.BOARD(
					SEQ
				  , TITLE
				  , WRITER
				  , CONTENT
				 )
		  VALUES(
		  			(SELECT IFNULL(MAX(SEQ), 0) + 1 FROM BOARD A)
		  		  , #{title}
		  		  , #{writer}
		  		  , #{content}
		  		)

mapper.update:
UPDATE BOARD
	SET 
		TITLE = #{title}
	  , CONTENT = #{content}
	WHERE SEQ = #{seq}
